body.script:
window.run = function() {
  var FooBar = Polymer({
    is: 'foo-bar',

    extends: 'iframe',

    created: function() {
      console.log("busted", this);
      debugger;
    }
  });

  var el1 = document.createElement('iframe');
  console.log("el1 is iframe", el1 instanceof HTMLIFrameElement);

  var el2 = document.createElement('iframe', 'foo-bar');
  console.log("el2 is iframe", el2 instanceof HTMLIFrameElement);
};
